let node = null


export function grabItem(id='')
{
    alert(id);
    let str_coordinates = id.split("-");
    const grid = store.getState().grid
    node = grid[str_coordinates[0]][str_coordinates[1]];

}
export function releaseItem()
{
    console.log("node released: ", node);
}

