import shallowCopy from "../../../helpers/2d-array-functions/shallow-copy"
import { checkNeighbors } from "./async-helpers/check-neighbors";
import { updateParent } from "./setters-and-getters/update-parent";

export default function* aStar(start_coordinates = [0, 0], end_cordinates = [3, 3], adjacency_matrix = [[]]) {
    let queue = []
    let parent_matrix = shallowCopy(adjacency_matrix, null);
    // let final_cost_matrix = shallowCopy(adjacency_matrix, Infinity);
    let visited = shallowCopy(adjacency_matrix, false);
    queue.push([Infinity, [...start_coordinates]]);

    while (queue.lenth !== 0) {
        let f_cost, current_vertex_coordinates = queue.shift();
        yield [...current_vertex_coordinates];
        visited[current_vertex_coordinates[0]][current_vertex_coordinates[1]] = true;
        const generator = checkNeighbors(current_vertex_coordinates, visited);
        let result = generator.next();

        while (!result.done) {

            let g_cost = manhattanDistance(current_vertex_coordinates, start_coordinates);
            let h_cost = manhattanDistance(current_vertex_coordinates, end_cordinates);
            f_cost = g_cost + h_cost;
            let value = result.value

            updateParent(current_vertex_coordinates, value, parent_matrix);
            queue.push([f_cost, value]);

            result = generator.next();
        }

        queue.sort(function (tuple1, tuple2) {
            if (tuple1[0] < tuple2[0]) {
                return -1
            }
            else {
                return 1;
            }
        });
    }

}

function manhattanDistance(coordinates1 = [], coordinates2 = []) {
    return Math.abs(coordinates1[0] - coordinates2[0]) + Math.abs(coordinates1[1] - coordinates2[1])
}
