import React from 'react';


import "./css/app.css";
import { library } from "@fortawesome/fontawesome-svg-core";
import { faCar } from "@fortawesome/free-solid-svg-icons";
import { faSquare } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import PathFindApp from './react/pages/pathfind-page';

library.add(faCar);
library.add(faSquare);


function App() {
  return (
    <div className="app">
      <div className="card">
        <h3>Path Finding Algorithm Visualizer</h3>
        <p>Imagine you are a driver for a ride share company. You wouldn't want to be driving in circles trying to get to your final destination, right? You would waste so much time and gas! </p>
        <p>This app shows an optimized path from getting from point A to point B.</p>
        <p>Note: Some Algorithms are more efficient than others!</p>

        <h3>Getting Started</h3>
        <p>• Choose an algorithm (A* is chosen by default)</p>
        <p>• Add an Item to the Grid (If you want)</p>
        <p>• Press play!</p>

        <h3>Key</h3>
        <ul>
          <li><FontAwesomeIcon className={"visited-true"} icon="square" />: Visiting To Calculate Distance - Like when your GPS is taking time to calculate the best route.</li>
          <li><FontAwesomeIcon className={"visited-true"} icon="square" />: Optimized Path - GPp\]

        S has calculated the best route!</li>
          <li><FontAwesomeIcon icon="car" />: Your Starting Place</li>
          <li><FontAwesomeIcon icon="flag" />: The Ending Place</li>
          <li><FontAwesomeIcon icon="street-view" />: Additional Destination - You need to pickup another passenger before going to the end, like sharing a ride to the same place.</li>
          <li><FontAwesomeIcon icon="square" />: Wall - You cannot drive through this, it is blocked off!</li>
          <li><FontAwesomeIcon icon="weight-hanging" />Weight - It takes more time to drive through this, imagine heavy traffic.</li>

        </ul>
      </div>
    <PathFindApp></PathFindApp>
    </div>
  );
}

export default App;
